import { IS_LOGIN_PERSON, IS_ADMIN_PERSON, GET_USER_ID, GET_TOKEN } from "./action-types";

const initState = {
  isAdmin: false,
  token: '',
  userId: '',
  isLogin: false
};

const authReducer = (state = initState, action: any) => {
  switch (action.type) {
    case IS_LOGIN_PERSON:
      return {
        ...state,
        isLogin: action.payload.isLogin
      }
    case IS_ADMIN_PERSON:
      return {
        ...state,
        isAdmin: action.payload.isAdmin
      }
    case GET_USER_ID:
      return {
        ...state,
        userId: action.payload.userId
      }
    case GET_TOKEN:
      return {
        ...state,
        userId: action.payload.token
      }
    default:
      return state
  }
}

export default authReducer;
