import {
    IonButton,
    IonContent,
    IonImg,
    IonInput,
    IonItem,
    IonLabel,
    IonTextarea,
} from '@ionic/react';
import React, { createRef, useState } from 'react';
import { Header } from '../../components/header';

interface IProduct {
    title: string,
    price: number,
    image: string,
    description: string
}

export const AddProduct: React.FC = () => {
    const fileInputRef: React.RefObject<HTMLInputElement> = createRef();
    const [imagePreview, setImagePreview] = useState('');
    const [product, setProduct] = useState({} as IProduct)

    const setValue = (event: Event) => {
        const target = event.target as HTMLInputElement;
        setProduct({
            ...product,
            [target.name]: target.value
        });
    }

    const filePicker = (event: Event) => {
        const file = (event.target as HTMLInputElement).files[0];
        setProduct({
            ...product,
            image: file
        });
        const reader = new FileReader();
        reader.onload = () => {
            setImagePreview(reader.result as string);
        };
        reader.readAsDataURL(file);
    }

    // const uploadImage = async () => {
    //     try {
    //         const data = new FormData();
    //         data.append("title", product.title);
    //         data.append("price", product.price);
    //         data.append("image", product.image, product.title);
    //         await request('/admin/addProduct', 'post', data, {
    //             Authorization: `Bearer ${auth.token}`
    //         });
    //     } catch(e) {}
    // }

    const filePickerClick = () => {
        (fileInputRef.current as HTMLElement).click();
    }

    return (
        <>
            <Header
                title={'Add product'}
                key={'AddProduct'}
            />
            <IonContent>
                <div>
                    <IonItem>
                        <IonLabel>Title:</IonLabel>
                        <IonInput name="title" type="text" onIonChange={setValue} />
                    </IonItem>
                    <IonItem>
                        <IonLabel>Price:</IonLabel>
                        <IonInput name="price" type="number" onIonChange={setValue} />
                    </IonItem>
                    <IonItem>
                        <IonButton color="warning" onClick={filePickerClick}>Change Image</IonButton>
                        <input name="image" type="file" ref={fileInputRef} onIonChange={filePicker} />
                    </IonItem>
                    <IonLabel>Image:</IonLabel>
                    {
                        imagePreview ?
                            <IonImg src={imagePreview} alt={product.title} />
                            : <h4>Please load image</h4>
                    }
                    <IonItem>
                        <IonLabel>Description:</IonLabel>
                        <IonTextarea name="description" onIonChange={setValue} />
                    </IonItem>
                </div>
            </IonContent>
        </>
    );
}
