import { IS_ADMIN, IS_LOGIN, USER_TOKEN, USER_EXPIRES_IN, USER_ID } from "../action-types";

export const isAdmin = (isAdmin: boolean) => {
    return (dispatch: any) => {
        dispatch({
            type: IS_ADMIN,
            payload: {
                isAdmin: isAdmin
            }
        });
    }
}

export const isLogin = (isLogin: boolean) => {
    return (dispatch: any) => {
        dispatch({
            type: IS_LOGIN,
            payload: {
                isLogin: isLogin
            }
        });
    }
}

export const userToken = (token: string) => {
    return (dispatch: any) => {
        dispatch({
            type: USER_TOKEN,
            payload: {
                token: token
            }
        });
    }
}

export const userExpiresIn = (expiresIn: string) => {
    return (dispatch: any) => {
        dispatch({
            type: USER_EXPIRES_IN,
            payload: {
                expiresIn: expiresIn
            }
        });
    }
}

export const userId = (userId: string) => {
    return (dispatch: any) => {
        dispatch({
            type: USER_ID,
            payload: {
                userId: userId
            }
        });
    }
}
