import { IonButton, IonContent, IonInput, IonItem, IonLabel } from '@ionic/react';
import React, { useState } from 'react';
import { useHistory } from 'react-router';
import { Header } from '../../components/header';
import { axios } from '../../axios';

export const LogIn = () => {

    const history = useHistory();
    const [authData, setAuthData] = useState({
        email: '',
        password: ''
    });

    const setValue = (event: Event) => {
        const target = event.target as HTMLInputElement;
        setAuthData({
            ...authData,
            [target.name]: target.value
        });
    }
    
    const identification = async () => {
        try {
            const res = await axios.post('/user/log-in', authData, { withCredentials: true });
            // await dispatch(authActions(res.data.admin, res.data.token, res.data.expiresIn, res.data.userId, res.data.isLogin));
            history.push('/');
        } catch(e) {}
    }

    return (
        <>
            <Header
                title={'LogIn'}
                key={'LogIn'}
            />
            <IonContent>
                <IonItem>
                    <IonLabel>Email:</IonLabel>
                    <IonInput name="email" type="email" onIonChange={setValue} />
                </IonItem>
                <IonItem>
                    <IonLabel>Password:</IonLabel>
                    <IonInput name="password" type="password" onIonChange={setValue} />
                </IonItem>
                <IonButton
                    color="warning"
                    onClick={identification}
                    className="btnMaxWidth btnBottom"
                >
                    Accept
                </IonButton>
            </IonContent>
        </>
    );
}
